    private void btn_ConsultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_ConsultarActionPerformed
        int idPessoa = Integer.parseInt(txt_IDPessoa.getText());
        PessoaDAO pDAO = new PessoaDAO();
        
        
        
        Pessoa p = pDAO.getPessoa(idPessoa);
        if(p == null){
            limparFormulario();
            JOptionPane.showMessageDialog(this, "Pessoa não encontrada!");
        }
        else{
            txt_IDPessoa.setText(String.valueOf(p.getId()));
            txt_Nome.setText(p.getNome());
            buttonGroup1.clearSelection();
            cmb_Idioma.setSelectedItem(p.getIdioma());
            
            if("Masculino".equalsIgnoreCase(p.getSexo())){
                rdb_Masculino.setSelected(true);
            
            }else if("Feminino".equalsIgnoreCase(p.getSexo())){
                rdb_Feminino.setSelected(true);
            }
            
            cmb_Idioma.setSelectedItem(p.getIdioma());
        }
    }//GEN-LAST:event_btn_ConsultarActionPerformed

