    private void btnAtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtualizarActionPerformed
        Pessoa p = new Pessoa();
        p.setId(Integer.parseInt(txt_IDPessoa.getText()));
        p.setNome(txt_Nome.getText());
        p.setSexo(rdb_Masculino.isSelected() ? "Masculino" : "Feminino");
        p.setIdioma((String) cmb_Idioma.getSelectedItem());
        
        PessoaDAO pDAO = new PessoaDAO();
        pDAO.editar(p);
        
        limparFormulario();
        
    }//GEN-LAST:event_btnAtualizarActionPerformed
    private void limparFormulario(){
        txt_IDPessoa.setText("");
        txt_Nome.setText("");
        buttonGroup1.clearSelection();
        cmb_Idioma.setSelectedItem("");
        }
    private void btn_LimparActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_LimparActionPerformed
        limparFormulario();
    }//GEN-LAST:event_btn_LimparActionPerformed

    private void btn_excluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_excluirActionPerformed
        int resposta = JOptionPane.showConfirmDialog(null, "Deseja realmente excluir essa pessoa?",
                "Exclusão", JOptionPane.YES_NO_OPTION);
        
        if(resposta == JOptionPane.YES_OPTION){
            PessoaDAO pDAO = new PessoaDAO();
            pDAO.excluir(Integer.parseInt(txt_IDPessoa.getText()));
            
            limparFormulario();
        }
    }//GEN-LAST:event_btn_excluirActionPerformed

